<?phpsession_start();require('include/connectdb.php');$comments = filter_var(trim($_POST['text']),FILTER_SANITIZE_STRING);if (isset($_SESSION['user_name'])){    $commenter = $_SESSION['user_name'];    $user_id = $_SESSION['user_id'];    $query = "INSERT INTO comments (comments, user_id ) VALUES ('{$comments}', '{$user_id}')";}else{    $commenter = filter_var(trim($_POST['name']),FILTER_SANITIZE_STRING);    $query = "INSERT INTO comments (comments, commenter) VALUES ('{$comments}','{$commenter}')";}if (($comments == '') || ($commenter == '')){    $_SESSION['comment'] = 'error';    header('location: /');    die();}$create = mysqli_query($connect, $query);if (!$create){    $_SESSION['comment'] = 'error';}else{    $_SESSION['comment'] = 'ok';}mysqli_close($connect);header('location: /');